import { useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import * as C from './App.styles';

function App() {
  const [data, setData] = useState({});
  const [location, setLocation] = useState('');
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=ebf6d40b6356a657fa7a2208a02e3bbd&units=metric`;

  const searchLocation = (event) => {
    if (event.key === 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data);
        console.log(response.data);
      });
      setLocation('');
    }
  };

  return (
    <>
      <C.Container>
        <C.Input
          placeholder="Choose City"
          type="text"
          value={location}
          onKeyPress={searchLocation}
          onChange={(event) => setLocation(event.target.value)}
        />
        <C.Card>
          <C.Content>
            <C.Name>Rio de Janeiro</C.Name>

            {data.main ? <C.Temperature>{data.main.temp.toFixed(0)}째</C.Temperature> : null}

            <C.Description>Sempre quente</C.Description>
            <C.BottomDiv>
              <div>
                Min <span>26째</span>
              </div>
              <div>
                Max <span>26째</span>
              </div>
              <div>
                Feels Like <span>26째</span>
              </div>
            </C.BottomDiv>
          </C.Content>
        </C.Card>
      </C.Container>
    </>
  );
}

export default App;
